mapper.select:
select user_id, user_name
from users
where user_id = #{userId} and user_password = #{userPassword}

select user_id, user_password, user_name, email_id, email_domain, join_date
from users
where user_id = #{userId}

mapper.update:
update users
user_password = #{userPassword}, user_name = #{userName}, email_id = #{emailId}, email_domain = #{emailDomain},
where user_id = #{userId}

mapper.select:
select user_name, user_id, email_id, email_domain, join_date
from users
where user_id = #{userId}

mapper.update:
update users
set token = #{token}
where user_id = #{userId}

mapper.select:
select token
from users
where user_id = #{userId}

mapper.update:
update users
set token = #{token, jdbcType=VARCHAR}
where user_id = #{userId}
